rule.definition:
#"SYSTEM_SYSRULES_richTextDisplayField"(
  label: "Filters",
  labelPosition: "ADJACENT",
  value: {
    if(isnull(ri!creator),
      {},
      {
        #"SYSTEM_SYSRULES_richTextItem"(text: "Creator: " & #"97597486-ded5-4b50-a2f8-f297f610d000_FORMATNAME"(ri!creator)),
        char(10)
      }
    ),
    if(isnull(ri!creationTime),
      {},
      {
        #"SYSTEM_SYSRULES_richTextItem"(text: "Created In: " & #"_a-0001dda0-2f73-8000-aae2-01ef9001ef90_16229"(eomonth(today(),ri!creationTime))),
        char(10)
      }
    ),
    if(isnull(ri!status),
      {},
      {
        #"SYSTEM_SYSRULES_richTextItem"(text: "Status: " & ri!status),
        char(10)
      }
    ),
    if(isnull(ri!priority),
      {},
      {
        #"SYSTEM_SYSRULES_richTextItem"(text: "Priority: " & ri!priority),
        char(10)
      }
    ),
    if(
      all(fn!isnull, {ri!creator, ri!creationTime, ri!status, ri!priority, ri!searchTerm}),
      #"SYSTEM_SYSRULES_richTextItem"(text: "<no filter applied>", style: "EMPHASIS"),
      {
        #"SYSTEM_SYSRULES_richTextItem"(
          text: "(Clear Filters and Search)",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: null,
            saveInto: {
              ri!creator,
              ri!creationTime,
              ri!status,
              ri!priority,
              ri!searchTerm
            }
          )
        )
      }
    )
  }
)
